// Format all 5 analyst outputs for the synthesis agent
const items = $input.all();

let summary = "# INVESTMENT COMMITTEE ANALYST REPORTS\n\n";

const analysts = [
  "Market Sizing Analyst",
  "Founder Fit Analyst", 
  "Product-Market Fit Analyst",
  "Competitive Moat Analyst",
  "Valuation Analyst"
];

for (let i = 0; i < items.length && i < 5; i++) {
  summary += `## ${analysts[i]}\n\n`;

  // Handle the nested output structure
  let output = 'No output';

  if (items[i].json.output && Array.isArray(items[i].json.output)) {
    // Output is an array with nested objects
    output = items[i].json.output[0]?.output || 
             items[i].json.output[0]?.text ||
             JSON.stringify(items[i].json.output[0]);
  } else if (items[i].json.output) {
    // Output is directly available
    output = items[i].json.output;
  } else if (items[i].json.text) {
    output = items[i].json.text;
  }

  summary += `**Analysis Output:**\n${output}\n\n`;
  summary += "─".repeat(60) + "\n\n";
}

summary += `\n**Total Analysts Reporting:** ${items.length}\n`;

// Try to get chatId from items if available
let chatId = null;
if (items.length > 0) {
  chatId = items[0].json.chatId || null;
}

return {
  json: {
    committee_summary: summary,
    analyst_count: items.length,
    chatId: chatId
  }
};
